import styled, { css } from 'styled-components';
import Tooltip from '../Tooltip';

interface ContainerProps {
  isFocused: boolean;
  isFilled: boolean;
  isErrored: boolean;
}
export const Container = styled.div<ContainerProps>`
  background: #232129;
  border: 0;
  border-radius: 10px;
  padding: 16px;
  width: 100%;
  border: 2px solid #232129;
  display: flex;
  align-items: center;
  color: #666360;
  & + div {
    margin-top: 6px;
  }
  input {
    flex: 1;
    background: transparent;
    border: 0;
    color: #666360;
    &::placeholder {
      color: #666360;
    }
  }
  svg {
    margin-right: 16px;
    ${props =>
      props.isErrored &&
      css`
        fill: #c53030;
      `}
  }
  ${props =>
    props.isErrored &&
    css`
      border-color: #c53030;
    `}
  ${props =>
    props.isFocused &&
    css`
      color: #ff9000;
      border-color: #ff9000;
    `}
  ${props =>
    props.isFilled &&
    css`
      color: #ff9000;
    `}
`;
export const Error = styled(Tooltip)`
  height: 20px;
  svg {
    margin: 0px;
  }
  span {
    background: #c53030;
    color: #fff;
    &::before {
      border-color: #c53030 transparent;
    }
  }
`;
