import styled from 'styled-components';

export const Container = styled.div`
  background: #232129;
  border: 0;
  border-radius: 10px;
  padding: 16px;
  width: 100%;
  border: 2px solid #232129;
  display: flex;
  align-items: center;
  color: #666360;
  input {
    flex: 1;
    background: transparent;
    border: 0;
    color: #666360;
    &::placeholder {
      color: #666360;
    }
    & + input {
      margin-top: 8px;
    }
  }
  svg {
    margin-right: 16px;
  }
`;
