import styled from 'styled-components';
import signInBackground from '../../assets/sign-in-background.png';

export const Background = styled.div`
  flex: 1;
  background: url(${signInBackground}) no-repeat center;
  background-size: cover;
`;
export const Container = styled.div`
  height: 100vh;
  display: flex;
  align-items: stretch;
`;
export const Content = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 700px;
  form {
    margin: 80px 0;
    width: 340px;
    text-align: center;
    h1 {
      margin-bottom: 24px;
    }
    input {
      flex: 1;
      background: #232129;
      border: 0;
      border-radius: 10px;
      padding: 16px;
      width: 100%;
      border: 2px solid #232129;
      color: #666360;
      display: flex;
      align-items: center;
      &::placeholder {
        color: #666360;
      }
      & + input {
        margin-top: 8px;
      }
    }
    button {
      flex: 1;
      background: #ff9000;
      height: 56px;
      border-radius: 10px;
      padding: 0 16px;
      width: 100%;
      color: #312e38;
      margin-top: 8px;
      border: none;
    }
    a {
      color: #f4ede8;
      display: block;
      margin-top: 24px;
      text-decoration: none;
      transition: color 0.3s;
    }
  }
  > a {
    color: #ff9000;
    display: block;
    margin-top: 24px;
    text-decoration: none;
    transition: color 0.3s;
    display: flex;
    align-items: center;
    svg {
      margin-right: 16px;
    }
  }
`;
