import styled from 'styled-components';
import { shade } from 'polished';

export const Container = styled.button`
  flex: 1;
  background: #ff9000;
  height: 56px;
  border-radius: 10px;
  padding: 0 16px;
  width: 100%;
  color: #312e38;
  margin-top: 8px;
  border: 0;
  transition: background-color 0.2s;
  &:hover {
    background: ${shade(0.2, '#ff9000')};
  }
`;
